        $time = current_time('mysql');

        wp_update_post( array (
            'ID'            => $post_id, 
            'post_date'     => $time,
            'post_date_gmt' => get_gmt_from_date( $time ),
            'post_modified' => $time,
            'post_modified_gmt' => get_gmt_from_date($time),
        )  );
