def parse(row):
    s = row.prod_name
    matches = re.findall('\d+g', s)
    if matches:
        if len(matches) == 1:
            return matches[0] #if you have a single match
        else:
            return 'parsing error' #if you have multiple unexpected matches
    return np.nan #no matches


df['pack'] = df.apply(parse, axis=1)

